async function check_itemAmounts_for_all_location(datex, items_array, locations_array, req, tx) {

  const query = `

  WITH previous_balance AS (
    SELECT 
        ah1.id,
        ah1.account_name as item_name,
        tb.item_location_id_tb,
        ah2.account_name as location_name,
        COALESCE(SUM(tb.item_amount), 0) AS amount
    FROM 
         transaction_body tb
        LEFT JOIN accounts_header ah1 ON ah1.id = tb.item_id
        LEFT JOIN accounts_header ah2 ON ah2.id = tb.item_location_id_tb
        LEFT JOIN transaction_header th ON th.id = tb.transaction_header_id
    WHERE 
        th.company_id = $1
      AND th.is_including_items IS true
      AND th.datex < $2
      AND tb.item_amount IS NOT null
      AND ah1.id IN (${items_array.join(',')}) -- AND ah1.id IN (195,189)
      AND tb.item_location_id_tb IN (${locations_array.join(',')}) --AND tb.item_location_id_tb IN (23,104)
    GROUP BY 
        ah1.id, tb.item_location_id_tb, ah2.account_name
    ),
    transaction_details AS (
        select
          ah1.id,
          ah1.account_name as item_name,
            th.id AS th_id,
            tb.item_location_id_tb as location_id,
            ah2.account_name as location_name,
            th.datex,
            th.transaction_type,
            tb.debit,
            tb.credit,
          COALESCE(tb.item_amount, 0) AS amount
        FROM 
            transaction_body tb
            left join accounts_header ah1 on ah1.id =  tb.item_id
            left join accounts_header ah2 on ah2.id =  tb.item_location_id_tb
            left join transaction_header th ON th.id = tb.transaction_header_id
        WHERE 
            th.company_id = $1
            AND th.datex >= $2
            AND th.is_including_items is true
          AND tb.item_amount IS NOT null
          AND ah1.id IN (${items_array.join(',')}) -- AND ah1.id IN (195,189)
          AND tb.item_location_id_tb IN (${locations_array.join(',')}) -- AND tb.item_location_id_tb IN (23,104)
        UNION ALL
        SELECT 
            0 AS id,
            pb.item_name,
            0 as th_id,
            pb.item_location_id_tb as location_id,
            pb.location_name,
            $2 AS datex, -- تاريخ بداية الفترة
            0 AS transaction_type,
            0 as debit,
            0 as credit,
          pb.amount AS amount
        FROM 
            previous_balance pb
    ),
    order_data as (
    select
      ROW_NUMBER() OVER (ORDER by
            id asc, location_id asc, datex ASC,
        CASE 
            -- 🟢 أولًا: العمليات التي تضيف للمخزون
            WHEN transaction_type = 0 THEN 1  -- ا
          WHEN transaction_type = 6 THEN 2  -- المشتريات
            WHEN transaction_type = 2 AND (amount > 0 OR debit > 0) THEN 3  -- قيد محاسبي يضيف مخزون
            WHEN transaction_type = 31 AND amount > 0 THEN 4  -- تصنيع - إضافة منتج نهائي
            WHEN transaction_type = 12 AND amount > 0 THEN 5  -- تحويلات المخزون - إضافة  ( بس مش بيأثر على التكلفه )
    
            -- 🔵 ثانيًا: العمليات التي تقلل المخزون بعد الإضافات
            WHEN transaction_type = 7 THEN 6  -- مرتجع مشتريات
            WHEN transaction_type = 2 AND (amount < 0 OR credit > 0) THEN 7  -- قيد محاسبي يسحب مخزون
            WHEN transaction_type = 31 AND amount < 0 THEN 8  -- تصنيع - استهلاك مواد خام
            WHEN transaction_type = 12 AND amount < 0 THEN 9  -- تحويلات المخزون - خصم  ( بس مش بيأثر على التكلفه )
    
    
            -- 🔴 ثالثًا: العمليات التي تسحب المخزون في النهاية (COGS)
            WHEN transaction_type = 3 THEN 10  -- المبيعات
            WHEN transaction_type = 4 THEN 11  -- مرتجع مبيعات
    
            -- ⏺️ أخيرًا: أي عمليات أخرى غير معروفة
            ELSE 12  
        END ASC,
            id ASC) AS serial_number,
      id,
      item_name,
      location_id,
      location_name,
      datex,
      th_id,
      transaction_type,
      amount
    from 	
      transaction_details
      
    )
        SELECT
            serial_number,
            id,
            item_name,
            location_id,
            location_name,
            datex,
            transaction_type,
            amount,
            SUM(amount) OVER (
              PARTITION BY id, location_id 
              ORDER BY serial_number ASC
          ) AS cumulative_balance
        FROM 
            order_data od
        --order by
          --serial_number DESC    -- شغال كويس بس سيبه عشان دالة فايند هتجيب اول نتيجه تقابلها فلازم نرتبه صح
  `;

  const params = [req.session.company_id, datex];

  const cumulative_array = await tx.any(query, params) || [];
  
  let result = true

  if (cumulative_array.length > 0){
    const negative_row = cumulative_array.find(item => +item.cumulative_balance < 0)
    if (negative_row){
      result = `سيكون هناك كمية سالبة للصنف '${negative_row.item_name}'  فى موقع  '${negative_row.location_name}'  فى تاريخ  '${negative_row.datex}'`
    }

  }

  return result;
}

async function update_items_cogs(items_array,datex, req, tx) {
  
  if (!items_array){
    return
  }


  if (!Array.isArray(items_array) || items_array.length === 0) {
    throw new Error(
      'حدث خطأ غير متوقع '
    );
    return;
  }

  await update_cogs_part1(items_array,datex, req, tx)

  const last_array =  await update_cogspart2(items_array,datex, req, tx)

  if (last_array.length > 0) {
    await update_cogs_part1(last_array,datex, req, tx)
  }
}

async function update_cogs_part1(items_array,datex, req, tx) {
  
  const query0 = `
  SELECT
    tb.item_id,
    SUM(tb.item_amount ) AS Current_amount,
    SUM(
        CASE
        WHEN tb.item_amount < 0 THEN -tb.cogs-- تخفيض في التكلفة
        ELSE tb.cogs -- زيادة في التكلفة		
        END			
    ) AS value
FROM
    transaction_body tb
INNER JOIN transaction_header th ON th.id = tb.transaction_header_id
inner join accounts_header ah on ah.id = tb.item_id
where
    ah.account_type_id = 5
    and th.datex < $1
    AND th.company_id = $2
    AND tb.item_id IN (${items_array.join(',')})
    AND th.is_deleted IS NULL
    AND th.is_including_items IS TRUE
    AND NOT (th.transaction_type IN (3,4) AND th.datex = $1) -- استثناء العمليات
GROUP by
tb.item_id
;`

  const started_balance = await tx.any(query0,[datex, req.session.company_id])  //! dayman 5aly el datex $1 3ashan mortpt be be el arkam fe ele est3lam 
    

const query1 = `
SELECT 
      tb.id,
      th.transaction_type,
      th.datex,
      tb.debit,
      tb.credit,
      tb.item_amount,
      tb.item_id,
      tb.cogs,
      tb.is_production_item
  FROM
      transaction_body tb
  LEFT JOIN 
      transaction_header th ON th.id = tb.transaction_header_id
  WHERE 
      th.company_id = $1
      AND th.is_deleted IS NULL
      AND tb.item_id is not null
      AND tb.item_id IN (${items_array.join(",")})
      --and tb.is_production_item is null
      AND th.datex >= $2
ORDER BY 
    th.datex ASC, 
    CASE 
        -- 🟢 أولًا: العمليات التي تضيف للمخزون
        WHEN th.transaction_type = 6 THEN 1  -- المشتريات
        WHEN th.transaction_type = 2 AND (tb.item_amount > 0 OR tb.debit > 0) THEN 2  -- قيد محاسبي يضيف مخزون
        WHEN th.transaction_type = 31 AND tb.item_amount > 0 THEN 3  -- تصنيع - إضافة منتج نهائي
        WHEN th.transaction_type = 12 AND tb.item_amount > 0 THEN 4  -- تحويلات المخزون - إضافة  ( بس مش بيأثر على التكلفه )

        -- 🔵 ثانيًا: العمليات التي تقلل المخزون بعد الإضافات
        WHEN th.transaction_type = 7 THEN 5  -- مرتجع مشتريات
        WHEN th.transaction_type = 2 AND (tb.item_amount < 0 OR tb.credit > 0) THEN 6  -- قيد محاسبي يسحب مخزون
        WHEN th.transaction_type = 31 AND tb.item_amount < 0 THEN 7  -- تصنيع - استهلاك مواد خام
        WHEN th.transaction_type = 12 AND tb.item_amount < 0 THEN 8  -- تحويلات المخزون - خصم  ( بس مش بيأثر على التكلفه )


        -- 🔴 ثالثًا: العمليات التي تسحب المخزون في النهاية (COGS)
        WHEN th.transaction_type = 3 THEN 9  -- المبيعات
        WHEN th.transaction_type = 4 THEN 10  -- مرتجع مبيعات

        -- ⏺️ أخيرًا: أي عمليات أخرى غير معروفة
        ELSE 11  
    END ASC,
    tb.id ASC
      ;
`;

  // جلب البيانات من قاعدة البيانات
  const items_transactions_array = await tx.any(query1,[req.session.company_id,datex]);
  
  let updatedRecords = [];

  for (const item_id of items_array){
  
  // تعريف المتغيرات لتتبع القيم
  let old_cogs = 0;
    
  let started_balances = []
  let started_amount = 0
  let started_value = 0
  started_balances = started_balance.find(item => +item.item_id === +item_id);
  
  if (started_balances){
    started_amount = +started_balances.current_amount || 0;
    started_value = +started_balances.value || 0;
  }


  const item_transaction_arry = items_transactions_array.filter(item => +item.item_id === +item_id)  

for (const row of item_transaction_arry) {
    
const type = +row.transaction_type
let cogs = 0;


  const row_amount = Math.abs(row.item_amount)

  if (type === 6 || (type === 2 && row.debit && !row.credit) || (type === 31 && row.is_production_item)){ // فاتورة مشتريات او قيد محاسبى مدين او صنف مصنع ياعمل على انه مشتريات
    cogs = +row.debit
    started_amount += +row_amount;
    started_value += +cogs;

    
    if (!(type === 31 && row.is_production_item)){
      updatedRecords.push({ id: row.id, cogs});
    }

  }else if(type === 7 || (type === 2 && row.credit && !row.debit)){ // مرتجع المشتريات قيد محاسبى دائن
    cogs = +row.credit
    started_amount -= +row_amount;
    started_value -= +cogs;
    updatedRecords.push({ id: row.id, cogs});
  }else if(type === 3 || (type === 31 && !row.is_production_item)){ // فاتورة مبيعات او اصناف مستهلكه فى فاتوره تصنيع
 
    if(+started_value === 0 || +started_amount === 0 || +row_amount === 0){
      cogs = 0  
    }else{
      cogs = parseFloat(((started_value / started_amount) * row_amount).toFixed(2));
    }
    started_amount -= +row_amount;
    started_value -= cogs;


    updatedRecords.push({ id: row.id, cogs});
  }else if(type === 4){ // مرتجع مبيعات
    if(+started_value === 0 || +started_amount === 0 || +row_amount === 0){
      cogs = 0  
    }else{
      cogs = parseFloat(((started_value / started_amount) * Math.abs(row_amount)).toFixed(2))
    }
    started_amount += +row_amount;
    started_value += cogs; // عملنا دى بالسالب لانى ضربت الكوجز فوق بالسالب  وبالتالى هيدينى زائد
    updatedRecords.push({id: row.id, cogs});
  }
  
  old_cogs += cogs;
}
}

// console.table(updatedRecords);
turn_EmptyValues_TO_null(updatedRecords)
 /* old
const queries = updatedRecords.map(
  ({ id, cogs }) =>
      `UPDATE transaction_body SET cogs = ${cogs} WHERE id = ${id}`
);
await tx.batch(queries.map((query) => tx.none(query)));
*/

if (updatedRecords.length > 0) {
  const ids = updatedRecords.map(({ id }) => id);

  
  const cases = updatedRecords
    .map(({ id, cogs }, index) => `WHEN id = $${index + 1} THEN ${cogs}`)
    .join(" ");

    
  const query = `
    UPDATE transaction_body
    SET cogs = CASE ${cases} END
    WHERE id IN (${ids.map((_, i) => `$${i + 1}`).join(", ")})
  `;

  await tx.none(query, ids);
}

}

async function update_cogspart2(items_array,datex, req, tx) {
  
//! update production_items after everything is done
let query2 = `
WITH balances AS (
    SELECT
        th.id AS transaction_header_id,
        SUM(
            COALESCE(tb.credit, 0) - COALESCE(tb.debit, 0) + COALESCE(tb.cogs, 0)
        ) AS new_total_cost
    FROM transaction_header th
    INNER JOIN transaction_body tb ON tb.transaction_header_id = th.id
    WHERE
        th.company_id = $1
        AND (
            th.transaction_type = 31 
            AND th.id IN (
                SELECT DISTINCT tb2.transaction_header_id
                FROM transaction_body tb2
                WHERE tb2.item_id IN (${items_array.join(',')})
                    AND tb.is_production_item IS NULL
            )
        )
        AND th.datex >= $2
    GROUP BY th.id
)
UPDATE transaction_body tb
SET 
    cogs = balances.new_total_cost,
    debit = balances.new_total_cost
FROM balances
WHERE tb.transaction_header_id = balances.transaction_header_id 
    AND tb.is_production_item = TRUE
RETURNING tb.item_id;
`;

let updatedItems = await tx.manyOrNone(query2, [req.session.company_id, datex]);

// استخراج الأصناف في مصفوفة
let updatedItemsArray = updatedItems.map(row => Number(row.item_id)) || [];

return updatedItemsArray 

}



